import React, {useState} from 'react';
import Review from './Review';
import people from './data'
import {FaChevronLeft, FaChevronRight, FaQuoteRight} from "react-icons/fa";

function App() {
    const [index, setIndex] = useState(0);
    const {name, job, image, text} = people[index];
    const prevPerson = (currentIndex) => {
        return currentIndex + 1;
    }
    const nextPerson = (currentIndex) => {
        return currentIndex - 1;
    }

    return <main>
        <article className='review'>
            <div className='img-container'>
                <img src={image} alt={name} className='person-img'/>
                <span className='quote-icon'>
                    <FaQuoteRight />
                </span>
            </div>
            <h4 className='author'>{name}</h4>
            <p className='job'>{job}</p>
            <p className='info'>{text}</p>
            <div className="btn-container">
                <button type='button' className='prev-btn' onClick={() =>  setIndex(prevPerson(index))}>
                    <FaChevronLeft />
                </button>
                <button type='button' className='next-btn' onClick={() => setIndex(nextPerson(index))}>
                    <FaChevronRight />
                </button>
            </div>
        </article>
    </main>;
}

export default App;
